,105,'_adinserter_block_exceptions',''),(1308,397,'_likes','1'),(1309,503,'_likes','1'),(1310,485,'_likes','1'),(1311,740,'_edit_last','1'),(1312,740,'_edit_lock','1505002986:1'),(1314,740,'_likes','3'),(1315,740,'views','166'),(1316,740,'_adinserter_block_exceptions',''),(1317,397,'_adinserter_block_exceptions',''),(1318,756,'_edit_last','1'),(1319,756,'_edit_lock','1507102228:1'),(1321,756,'_likes','1'),(1322,756,'views','345'),(1323,756,'_adinserter_block_exceptions',''),(1324,781,'_edit_last','1'),(1325,781,'_edit_lock','1523746339:1'),(1326,781,'_oembed_ab9e73722d4d40eb56799e8c5228301c','{{unknown}}'),(1328,781,'_likes','3'),(1329,781,'views','143'),(1330,781,'_adinserter_block_exceptions',''),(1331,798,'_edit_last','1'),(1332,798,'_edit_lock','1523746334:1'),(1333,798,'_adinserter_block_exceptions',''),(1335,798,'_likes','1'),(1336,798,'views','81'),(1337,808,'_edit_last','1'),(1338,808,'_edit_lock','1523746540:1'),(1339,808,'_adinserter_block_exceptions',''),(1341,808,'_likes','1'),(1342,808,'views','61'),(1343,503,'_oembed_4ad94e520d6d57b0dd4fc043494b6783','{{unknown}}');
